import {Link, useNavigate} from 'react-router-dom'
import {useEffect, useState} from 'react';
import axios from 'axios';
import swal from 'sweetalert2';

import {Container, Row, Col, Card, Button} from 'react-bootstrap'

let publicKey = 'a212261ae8786097770a9daab0441af7'

let privateKey = '99877ec4a33de704ee521320afb1c76214670452'

const marvelApi=()=>{


            const base = "https://gateway.marvel.com:443/v1/public/characters"
        const apiKeyParam = `?apikey=${publicKey}`
        
        

            const timestamp = new Date().getMilliseconds

            return (`&ts=${timestamp}&hash=` + (`${timestamp}${privateKey}${publicKey}`.md5Value))
        
    }

    console.log(marvelApi())
        
   
   
function Listado (props){
    
   
    const navigate= useNavigate();

    const [moviesList, setMoviesList] = useState([])

  


    useEffect(()=>{ 

        const endPoint='https://gateway.marvel.com:443/v1/public/series?apikey=a212261ae8786097770a9daab0441af7'

        

        axios.get(endPoint)
             .then(res=>{
                 const apiData= res.data;
                 console.log(apiData)
                 setMoviesList(apiData.results)
             })
             .catch(error=>{
                 swal(
                     <h2>Error en la carga de datos</h2>
                 );
                 return             })

     },[setMoviesList])

  
    
return(
<>
  
   
       <Container fluid="md" >

        <Row className="justify-content-md-center">

            {moviesList.map((oneMovie, idx)=>{

                return(
                    <Col md="auto m-4" key={idx}>
                    <Card style={{ width: '18rem' }}>
                        <Card.Img variant="top" style={{height:'75%'}} src={`https://image.tmdb.org/t/p/w400/${oneMovie.poster_path}`} alt='img_movie'/>
                        <button onClick={props.addOrRemovefromFavs} 
                        title='Agregar a favoritos' 
                        className='favourite_button'
                        data-movie-id={oneMovie.id}>
                        ðŸ–¤
                        </button>
                        <Card.Body>
                            <Card.Title><h5>{oneMovie.title.substring(0,200)}</h5></Card.Title>
                            <Card.Text>
                           {oneMovie.overview.substring(0,200)}...                            </Card.Text>
                           <Link to={`/detalle?movieID=${oneMovie.id}`} className='link'><Button variant="success">Ver detalle</Button></Link>
                        </Card.Body>
                        </Card>
                    </Col>

                )


            })}
           
            
        </Row>
    </Container>

</>
)
}

export default Listado;
