import { lazy, Suspense } from "react"
import  {Routes, Route, useLocation, Navigate} from "react-router-dom"
import { AnimatePresence, motion } from "framer-motion"


import "./App.css"
import { Login } from "./components/views/auth/Login/Login"
import { Tasks } from "./components/views/Tasks/Tasks"
import { Register } from "./components/views/auth/Register/Register"
import {TaskForm} from './components/TaskForm/TaskForm'
import {Intro} from "./components/views/auth/Login/Intro"

const Error404 = lazy(() => import("./components/views/Error404/Error404"))

const RequireAuth = ({ children }) => {
  if (!localStorage.getItem("token")) {
    return (<Navigate to="/login" replace={true} />)
  }
  return children
}

const pageTransition = {
  in: {
    opacity: 1,
  },
  out: {
    opacity: 0,
  },
}

export const App = () => {

  const location = useLocation()

  return (
    <AnimatePresence>
      <Routes location={location} key={location.pathname}>
        <Route path="/"
          element={
            <motion.div
              className="page"
              initial="out"
              animate="in"
              exit="out"
              variants={pageTransition}
            >
              <RequireAuth>
                <Tasks />
              </RequireAuth>
            </motion.div>
          } />
        
        <Route path="/"
          element={
            <motion.div
              className="page"
              initial="out"
              animate="in"
              exit="out"
              variants={pageTransition}
            >
              <RequireAuth>
                <TaskForm />
              </RequireAuth>
            </motion.div>
          } />
       <Route path="/newTask"
          element={
            <motion.div
              className="page"
              initial="out"
              animate="in"
              exit="out"
              variants={pageTransition}
            >
              <RequireAuth>
                <TaskForm />
              </RequireAuth>
            </motion.div>
          } />

        <Route path="/login" element={
          <motion.div
            className="page"
            initial="out"
            animate="in"
            exit="out"
            variants={pageTransition}
          >
            <Intro/>
            
          </motion.div>

        } />
         <Route path="/loginForm" element={
          <motion.div
            className="page"
            initial="out"
            animate="in"
            exit="out"
            variants={pageTransition}
          >
            <Login/>
            
          </motion.div>

        } />
        <Route path="/register" element={
          <motion.div
            className="page"
            initial="out"
            animate="in"
            exit="out"
            variants={pageTransition}
          >
            <Register />
          </motion.div>

        } />
        <Route path="*" element={
          <motion.div
            className="page"
            initial="out"
            animate="in"
            exit="out"
            variants={pageTransition}
          >
            <Suspense fallback={<>...</>}>
              <Error404 />
            </Suspense>
          </motion.div>
        } />
      </Routes>
    </AnimatePresence>
  )
}
