import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import {axios} from "axios"
import {useState, useEffect} from "react"

import "./Header.styles.css";
import logo from '../../img/Logo.jpg'

export const Header = () => {
  const navigate = useNavigate();

  const userName = localStorage.getItem('userName');
  const [data, setData] = useState({})

  const API_ENDPOINT = process.env.REACT_APP_API_ENDPOINT

  const { tasks } = useSelector((state) => {
    return state.tasksReducer;
  });

  useEffect(() => {
    axios.get(`${API_ENDPOINT}api/result`)
    
        .then((result) => {

          setData(result.data)}
        );
  }, [setData]);

  const renderImg=(userName)=>  {
     return data?.filter((data) => data.userName === userName)
    .map((data) => (
      <img src={data.img}></img>
       
    ))}  
    
  

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userName");
    navigate("/login", { replace: true });
  };

  return (
    <header>
      <img className='logo' src={logo} alt="Logo" />
     
      <div className="wrapper_right_header">
        
        <div className="textdata">Tareas creadas: {!tasks ? 0 : tasks.length}</div>
        <div className="textdata">Avenger: {userName}</div>
        <div>{renderImg}</div>
        <button className='buttonHeader' onClick={handleLogout}>Cerrar Sesi√≥n</button>
      </div>
       
    
    </header>
  );
};
