import { useState, useEffect } from 'react';

//Libraries
import {Routes, Route} from 'react-router-dom';

//Styles
import './app.css'

//Components
import Listado from './Listado'
import Header from './Header'
import Footer from './Footer'
import Detalle from './Detalle';
import Resultados from './Resultados';
import Favoritos from './Favoritos';


function Inicio() {

  const [favourites, setFavs]= useState([]);

  useEffect(()=>{

    const favsInLocal= localStorage.getItem('favs')
     
     if(favsInLocal!== null){
         const favsArray= JSON.parse(favsInLocal);
         setFavs(favsArray)       }
  },[])

  
  const addOrRemovefromFavs=(e)=>{

    const favCharacters= localStorage.getItem('favs')

    let tempCharacterInFavs;
  
    if(favCharacters===null){
      tempCharacterInFavs=[]
  
    } else{
      tempCharacterInFavs= JSON.parse(favCharacters)
    }
  
   
    const btn= e.currentTarget;
    const parentBtn= btn.parentElement;

    const imgUrl= parentBtn.querySelector('img').getAttribute('src');
    const userName=parentBtn.querySelector('h5').innerText
    const description=parentBtn.querySelector('p').innerText

    const characterData={
                    imgUrl, userName, description,
                    id:btn.dataset.characterId
                  }
    
    let characterIsInArray= tempCharacterInFavs.find(oneCharacter=>{
      return oneCharacter.id === characterData.id
    });

    if(!characterIsInArray){

      tempCharacterInFavs.push(characterData)
      localStorage.setItem('favs', JSON.stringify(tempCharacterInFavs));
      setFavs(tempCharacterInFavs)
      console.log('Se agreg贸 el personaje')
    } else {
      let characterLeft=tempCharacterInFavs.filter(oneCharacter=>{
        return oneCharacter.id !== characterData.id
      });
      localStorage.setItem('favs', JSON.stringify(characterLeft))
      setFavs(characterLeft)
      console.log('Se elimin贸 el personaje')
      }
    }





  return (
    <>
      <Header 
      favourites={favourites}/>
      
      <Routes>
      <Route path='/invitados' element={<Listado 
          addOrRemovefromFavs={addOrRemovefromFavs}/>}
      />
          <Route path='/invitados/detalle' element={<Detalle/>}/>
          <Route path='/invitados/resultados' element={<Resultados addOrRemovefromFavs={addOrRemovefromFavs}/>}/>
          <Route path='/invitados/favoritos' element={<Favoritos addOrRemovefromFavs={addOrRemovefromFavs} favourites={favourites}/>}/>
      </Routes>
      
      <Footer/>
    </>
  );
}

export default Inicio;
