import React, {useState, useEffect} from "react";
import { useFormik } from "formik";
import { useNavigate, Link } from "react-router-dom";
import * as Yup from "yup";
import { Carousel, CarouselItem } from 'react-round-carousel';
import { swal } from "../../../../utils/swal";
import axios from 'axios';

import "../Auth.styles.css";

import spiderman from '../../../../img/spiderman.png';
import blackwidow from '../../../../img/blackWidow.png';
import captainAmerican from '../../../../img/captainAmerica.png';
import hulk from '../../../../img/hulk.png';
import ironman from '../../../../img/ironman.png';


const { REACT_APP_API_ENDPOINT: API_ENDPOINT } = process.env;



export const Login = () => {
  const navigate = useNavigate();
  
  const [data, setData] = useState([]);

  const initialValues = {
    userName: "",
    password: "",
  }

  useEffect(() => {
    axios.get(`${API_ENDPOINT}api/result`)
    
        .then((result) => {
          console.log(result)
          setData(result.data)}
        );
  }, [setData]);


  const required = "* Campo obligatorio";

  const validationSchema = () =>
    Yup.object().shape({
      userName: Yup.string()
        .min(4, "La cantidad mÃ­nima de caracteres es 4")
        .required(required),
      password: Yup.string().required(required),
    });

  const onSubmit = () => {
    
    const { userName, password } = values;
   
    axios.post(`${API_ENDPOINT}api/login`, {
        userName,
        password,
      })
         
      .then((response) => {
        console.log(response)
        if (response.status === 200) {
          localStorage.setItem("token", response?.data?.token);
          localStorage.setItem("userId", response?.data?.userId);
          localStorage.setItem("userName", response?.data?.userName);
          navigate("/", { replace: true });
        } else {
          swal();
        }
      });
  };

  const formik = useFormik({ initialValues, validationSchema, onSubmit });

  const { handleSubmit, handleChange, errors, touched, handleBlur, values } =
    formik;

    const items: CarouselItem[] = Array(20)
  .fill('')
  .map((_: string, index: number) => ({
    alt: 'A random photo',
    image: `https://picsum.photos/${210 + index}`,
    content: (
      <div>
        <strong>Round Carousel</strong>
        <span>Slide number {index + 1}</span>
      </div>
    )
  }));
    const Images = [
      {
          title: 'Spider Man',
          url: {spiderman}
      },
      {
          title: 'Black Widow',
          url: {blackwidow}
      },
      {
          title: 'Hulk',
          url: {hulk}
      },
      {
         title: 'Iron Man',
          url: {ironman}
      },
  ]

  return (
    <div className='wrapperLogin'>
     <Carousel 
    imageList={Images}
    autoplay={Boolean}
    showArrows={Boolean}
    interval={1000}
    className={String}
    imageClassName={String}
    
    overlayClassName={String}
    id={String}
    />
       <div className="auth">
      
        <form onSubmit={handleSubmit}>
          <h1> Â¡Bienvenid@, Avenger!</h1>
          <div>
            <label>Â¿QuiÃ©n eres?</label>
            <select
            name="userName"
            onChange={handleChange}
            value={values.userName}
            className={errors.userName && touched.userName ? "error" : ""}
            onBlur={handleBlur}
          >
            <option value="">Selecciona tu nombre...</option>
            {data?.map((option, id) => {
            return(
                <option key={id} value={option.userName}>
                {option.userName}
              </option>)
})}
          </select>
          </div>
          <div>
            <label>ContraseÃ±a</label>
            <input
              type="password"
              name="password"
              onChange={handleChange}
              value={values.password}
              onBlur={handleBlur}
              className={errors.password && touched.password ? "error" : ""}
            />
            {errors.password && touched.password && (
              <div>{errors.password}</div>
            )}
          </div>
          <div>
            <button type="submit">Enviar</button>
            
          </div>
          <div className='wrapperList'>
            <h5 style={{textAlign: 'center', margin:'20px 0 10px 0 ', padding: '0'}}>Â¿AÃºn no configuraste tu cuenta?</h5>
            <Link to="/register">Registrate</Link>
          </div>
        </form>
      </div>
    </div>
  );
};
