import React, { useState, useEffect } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom"
import axios from 'axios';


import "../Auth.styles.css";
import logocompleto from '../../../../img/logocompleto.png'
import { swal2 } from "../../../../utils/swal2.jsx";


const { REACT_APP_API_ENDPOINT } = process.env

export const Register = () => {
  const [data, setData] = useState();
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`${REACT_APP_API_ENDPOINT}api/result`)
    
    .then((result) => {

      setData(result.data)}
    );
}, [setData]);

  const initialValues = {
    userName: "",
    password: "",
    confPassword: "",
    email: "",
    role: "",
      };

  const required = "*Campo obligatorio";

  const validationSchema = () =>
    Yup.object().shape({
      userName: Yup.string()
        .min(4, "La cantidad mínima de caracteres es 4")
        .required(required),
      password: Yup.string().required(required),
      confPassword: Yup.string().required(required),
      email: Yup.string().email("Debe ser un email válido").required(required),
      role: Yup.string().required(required),
            
    });


  const handleSubmit = (e) => {
  e.preventDefault()

    
    fetch(`${REACT_APP_API_ENDPOINT}api/register`, {
       method: "POST",
       headers: {
      "Content-Type": "application/json",
    },
        body: JSON.stringify({
        user: {
          userName: values.userName,
          password: values.password,
          confPassword: values.confPassword,
          email: values.email,
          role: values.role,
             },
      }),
    })
      .then((response) => {
        response.json()
        
      
      })
      .then((data) =>{
        
          swal2()
          navigate("/login", { replace: true });
         
      })
      .catch(error=>console.log(error));
  }

  const formik = useFormik({ initialValues, validationSchema});

  const {
    handleChange,
    errors,
    touched,
    handleBlur,
    values,
    } = formik;

  return (
    <div className="auth">
       <img className='logo_completo flip-vertical-fwd' src={logocompleto} alt='logo'></img>
      <form onSubmit={handleSubmit}>
        <h1 style={{textAlign: 'center'}}>Registro</h1>
        <div>
          <label>¿Cuál es tu nombre?</label>
          <select
            name="userName"
            onChange={handleChange}
            value={values.userName}
            className={errors.userName && touched.userName ? "error" : ""}
            onBlur={handleBlur}
          >
            <option value="">Selecciona tu nombre...</option>
            {data?.userName?.map((option) => (
              <option value={option} key={option}>
                {option}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Contraseña</label>
          <input
            type="password"
            name="password"
            onChange={handleChange}
            value={values.password}
            className={errors.password && touched.password ? "error" : ""}
            onBlur={handleBlur}
          />
          {errors.password && touched.password && (
            <span className="error-message">{errors.password}</span>
          )}
        </div>

        <div>
          <label>Confirme la contraseña</label>
          <input
            type="password"
            name="confPassword"
            onChange={handleChange}
            value={values.confPassword}
            className={errors.confPassword && touched.confPassword ? "error" : ""}
            onBlur={handleBlur}
          />
          {errors.confPassword && touched.confPassword && (
            <span className="error-message">{errors.confPassword}</span>
          )}
        </div>
        <div>
          <label>Email</label>
          <input
            type="email"
            name="email"
            onChange={handleChange}
            value={values.email}
            className={errors.email && touched.email ? "error" : ""}
            onBlur={handleBlur}
          />
          {errors.email && touched.email && (
            <span className="error-message">{errors.email}</span>
          )}
        </div>
        
        <div>
          <label>Rol</label>
          <select
            name="role"
            onChange={handleChange}
            value={values.role}
            className={errors.role && touched.role ? "error" : ""}
            onBlur={handleBlur}
          >
            <option value="">Seleccionar rol...</option>
            {data?.role?.map((option) => (
              <option value={option} key={option}>
                {option}
              </option>
            ))}
          </select>
          {errors.role && touched.role && (
            <span className="error-message">{errors.role}</span>
          )}
        </div>
        
        <div>
          <button type="submit">Enviar</button>
        </div>
        <div className='wrapperList'>
            <h5 style={{textAlign: 'center', margin:'20px 0 10px 0 ', padding: '0'}}>¿Ya estás registrado?</h5>
            <Link to="/login">Inicia sesión</Link>
          </div>
      </form>
    </div>
  );
};
