import React, {useState, useEffect} from "react";
import { useFormik } from "formik";
import { useNavigate, Link } from "react-router-dom";
import * as Yup from "yup";
import { swal } from "../../../../utils/swal";
import axios from 'axios';

import "../Auth.styles.css";
import avengers from '../../../../img/avengers.png';
import spiderman from '../../../../img/spiderman.png';
import blackwidow from '../../../../img/blackWidow.png';
import captainAmerica from '../../../../img/captainAmerica.png';
import hulk from '../../../../img/hulk.png';
import ironman from '../../../../img/ironman.png';
import thor from '../../../../img/thor.png';
import hawkeye from '../../../../img/hawkeye.png';


const { REACT_APP_API_ENDPOINT: API_ENDPOINT } = process.env;



export const Login = () => {
  const navigate = useNavigate();
  
  const [data, setData] = useState([]);

  const initialValues = {
    userName: "avengers",
    password: "",
  }

  useEffect(() => {
    axios.get(`${API_ENDPOINT}api/result`)
    
        .then((result) => {
          console.log(result)
          setData(result.data)}
        );
  }, [setData]);

    function renderImg (param){
    switch(param){
     case 'Spider Man':
        return 
     case 'Iron Man':
      return <img className='equipo ' src={ironman} alt="Iron Man" />;
     case 'Black Widow':
        return <img className='equipo scale-up-center' onLoad={window.replace.location()} src={blackwidow} alt="Black Widow" />;
     case 'Hulk':
          return <img className='equipo 'onLoad={window.replace.location()} src={hulk} alt="Hulk" />;
     case 'Capitán América':
          return <img className='equipo ' onLoad={window.replace.location()}src={captainAmerica} alt="Captain America" />;
    case 'Hawk Eye':
            return <img className='equipo 'onLoad={window.replace.location()}src={hawkeye} alt="Hawk Eye" />;
     case 'Thor':
              return <img className='equipo ' onLoad={window.replace.location()}src={thor} alt="Thor" />;
      default:
        return <img className='equipo ' onLoad={window.replace.location()} src={avengers} alt="Spider Man" />
    }
    
   }



  const required = "* Campo obligatorio";

  const validationSchema = () =>
    Yup.object().shape({
      userName: Yup.string()
        .min(4, "La cantidad mínima de caracteres es 4")
        .required(required),
      password: Yup.string().required(required),
    });

  const onSubmit = () => {
    
    const { userName, password } = values;
   
    axios.post(`${API_ENDPOINT}api/login`, {
        userName,
        password,
      })
         
      .then((response) => {
        console.log(response)
        if (response.status === 200) {
          localStorage.setItem("token", response?.data?.token);
          localStorage.setItem("userId", response?.data?.userId);
          localStorage.setItem("userName", response?.data?.userName);
          navigate("/", { replace: true });
        } else {
          swal();
        }
      });
  };

  const formik = useFormik({ initialValues, validationSchema, onSubmit });

  const { handleSubmit, handleChange, errors, touched, handleBlur, values } =
    formik;

   

  return (
    <div className='wrapperLogin'>
      <div className='scale up center'>
        {renderImg(values.userName)}
      </div>
       <div className="auth">
      
        <form onSubmit={handleSubmit}>
          <h1> ¡Bienvenid@, Avenger!</h1>
          <div>
            <label>¿Quién eres?</label>
            <select
            name="userName"
            onChange={handleChange}
            value={values.userName}
            className={errors.userName && touched.userName ? "error" : ""}
            onBlur={handleBlur}
          >
            <option value="">Selecciona tu nombre...</option>
            {data?.map((option, id) => {
            return(
                <option key={id} value={option.userName}>
                {option.userName}
              </option>)
})}
          </select>
          </div>
          <div>
            <label>Contraseña</label>
            <input
              type="password"
              name="password"
              onChange={handleChange}
              value={values.password}
              onBlur={handleBlur}
              className={errors.password && touched.password ? "error" : ""}
            />
            {errors.password && touched.password && (
              <div>{errors.password}</div>
            )}
          </div>
          <div>
            <button type="submit">Enviar</button>
            
          </div>
          <div className='wrapperList'>
            <h5 style={{textAlign: 'center', margin:'20px 0 10px 0 ', padding: '0'}}>¿Aún no configuraste tu cuenta?</h5>
            <Link to="/register">Registrate</Link>
          </div>
        </form>
      </div>
    </div>
  );
};
