import React from "react";
import { useFormik } from "formik";
import { useNavigate, Link } from "react-router-dom";
import * as Yup from "yup";

import { swal } from "../../../../utils/swal";
import axios from 'axios';

import "../Auth.styles.css";

import logocompleto from '../../../../img/logocompleto.png'

const { REACT_APP_API_ENDPOINT: API_ENDPOINT } = process.env;

export const Login = () => {
  const navigate = useNavigate();
  
  const [data, setData] = useState();

  useEffect(() => {
    axios(`${API_ENDPOINT}api/data`)
      .then((response) => response.json())
      .then((data) => setData(data.result));
  }, []);


  const required = "* Campo obligatorio";

  const validationSchema = () =>
    Yup.object().shape({
      userName: Yup.string()
        .min(4, "La cantidad mínima de caracteres es 4")
        .required(required),
      password: Yup.string().required(required),
    });

  const onSubmit = () => {
    const { userName, password } = values;

    if (data.status_code === 200) {
          localStorage.setItem("token", data?.result?.token);
          localStorage.setItem("userName", data?.result?.userName);
          navigate("/", { replace: true });
        } else {
          swal();
        }
    
         
  };

  const formik = useFormik({ initialValues, validationSchema, onSubmit });

  const { handleSubmit, handleChange, errors, touched, handleBlur, values } =
    formik;

  return (
    <>
        
      <div className="auth">
      <img className='logo_completo' src={logocompleto} alt='logo'></img>
        <form onSubmit={handleSubmit}>
          <h1>Iniciar sesión</h1>
          <div>
            <label>¿Quién eres?</label>
            <select
            name="userName"
            onChange={handleChange}
            value={values.userName}
            className={errors.userName && touched.userName ? "error" : ""}
            onBlur={handleBlur}
          >
            <option value="">Selecciona tu nombre...</option>
            {data?.userName?.map((option) => (
              <option value={option} key={option}>
                {option}
              </option>
            ))}
          </select>
          </div>
          <div>
            <label>Contraseña</label>
            <input
              type="password"
              name="password"
              onChange={handleChange}
              value={values.password}
              onBlur={handleBlur}
              className={errors.password && touched.password ? "error" : ""}
            />
            {errors.password && touched.password && (
              <div>{errors.password}</div>
            )}
          </div>
          <div>
            <button type="submit">Enviar</button>
            
          </div>
          <div>
            <button><Link to="/register" className='button'>Registrarme</Link></button>
          </div>
        </form>
      </div>
    </>
  );
};
