import {Link} from 'react-router-dom'
import {useEffect, useState} from 'react';
import axios from 'axios';
import swal from 'sweetalert2';
import {Container, Row, Col, Card, Button} from 'react-bootstrap'

let api_marvel = 'http://localhost:8080/api/result'

   
   
function Listado (props){
    
   

    const [characterList, setcharacterList] = useState([])

  
    useEffect(()=>{ 

        axios.get(api_marvel)
             .then(res=>{
                console.log(res.data)
                 setcharacterList(res.results)}
                
             )
             .catch(error=>{
                 swal(
                     <h2>Error en la carga de datos</h2>
                 );
                 return })

     },[setcharacterList, characterList])

  
    
return(
<>
  
   
       <Container fluid="md" >

        <Row className="justify-content-md-center">

            {characterList.map((oneCharacter, id)=>{

                return(
                    <Col md="auto m-4" key={id}>
                    <Card style={{ width: '18rem' }}>
                        <Card.Img variant="top" style={{height:'75%'}} src={oneCharacter.img} alt='img_character'/>
                        <button onClick={props.addOrRemovefromFavs} 
                        title='Agregar a favoritos' 
                        className='favourite_button'
                        data-character-id={oneCharacter._id}>
                        ðŸ–¤
                        </button>
                        <Card.Body>
                            <Card.Title><h5>{oneCharacter.userName}</h5></Card.Title>
                            <Card.Text>
                           {oneCharacter.description.substring(0,200)}...                           
                            </Card.Text>
                           <Link to={`/invitados/detalle?characterID=${oneCharacter._id}`} className='link'><Button variant="success">Ver detalle</Button></Link>
                        </Card.Body>
                        </Card>
                    </Col>

                )


            })}
           
            
        </Row>
    </Container>

</>
)
}

export default Listado;
