import { TASKS_REQUEST, TASKS_SUCCESS, TASKS_FAILURE } from "../types"
import axios from 'axios'

const { REACT_APP_API_ENDPOINT: API_ENDPOINT } = process.env

export const tasksRequest = () => ({
   type: TASKS_REQUEST,
})

export const tasksSuccess = data => ({
   type: TASKS_SUCCESS,
   payload: data,
})

export const tasksFailure = error => ({
   type: TASKS_FAILURE,
   payload: error,
})

let path = localStorage.getItem('userId')



export const getTasks = path => dispatch =>{
   const token = localStorage.getItem('token')
   dispatch(tasksRequest())
   axios.get(`${API_ENDPOINT}api/task/list/${path}`, {
       headers:  {
         'Authorization': `Bearer ${token}`,
        }            
         
     
   })
      .then(response => {
         const data = response.data
         dispatch(tasksSuccess(data))
      })
      .catch(error => dispatch(tasksFailure(error)))
}

export const deleteTask = id => dispatch => {
   const token = localStorage.getItem('token')
   axios.delete(`${API_ENDPOINT}api/task/delete/${id}`, {
       headers:  {
         'Authorization': `Bearer ${token}`,
        }            
         
     })
      .then(data => {
         
      console.log(data)
         dispatch(getTasks(""))}
         )
      .catch(error => dispatch(tasksFailure(error)))

}

export const editTaskStatus = data => dispatch => {
   const statusArray = ["NEW", "IN PROGRESS", "FINISHED"]
   const token = localStorage.getItem('token')
   const newStatusIndex =
      statusArray.indexOf(data.status) > 1
         ? 0
         : statusArray.indexOf(data.status) + 1

   axios.patch(`${API_ENDPOINT}api/task/${data.id}`, {title: data.title,
      importance: data.importance,
      status: statusArray[newStatusIndex],
      description: data.description,
      userName: data.userName},
       {
      headers:  {
         'Authorization': `Bearer ${token}`,
        }            
         
      })
      
      .then(data => dispatch(getTasks("")))
      .catch(error => dispatch(tasksFailure(error)))
}
