import React, { useState, useEffect } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import { Link } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";
import { Switch, FormControlLabel } from "@mui/material";
//import { useNavigate } from "react-router-dom";
import axios from 'axios';

import "../Auth.styles.css";


const { REACT_APP_API_ENDPOINT } = process.env

export const Register = () => {
  const [data, setData] = useState();
//  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`${REACT_APP_API_ENDPOINT}api/result`)
    .then((result) => {
      const resultData=result.data
    setData(resultData)}
    );
}, [setData]);

  const initialValues = {
    userName: "",
    password: "",
    email: "",
    teamID: "",
    role: "",
    continent: "",
    region: "",
    switch: false,
  };

  const required = "* Campo obligatorio";

  const validationSchema = () =>
    Yup.object().shape({
      userName: Yup.string()
        .min(4, "La cantidad mínima de caracteres es 4")
        .required(required),
      password: Yup.string().required(required),
      email: Yup.string().email("Debe ser un email válido").required(required),
      role: Yup.string().required(required),
      continent: Yup.string().required(required),
      region: Yup.string().required(required),
    });


  const onSubmit = () => {
    const teamID = !values.teamID ? uuidv4() : values.teamID;
    console.log(teamID);

    // axios.post(`S{REACT_APPI_ENDPOINT}auth/register`, {
    //     body: JSON.stringify({
    //     user: {
    //       userName: values.userName,
    //       password: values.password,
    //       email: values.email,
    //       teamID,
    //       role: values.role,
    //       continent: values.continent,
    //       region: values.region,
    //     },
    //   }),
    // })
    //   .then((response) => response.json())
    //   .then((data) =>
    //     navigate("/registered/" + data?.result?.user?.teamID, {
    //       replace: true,
    //     })
    //   );
  };

  const formik = useFormik({ initialValues, validationSchema, onSubmit });

  const {
    handleSubmit,
    handleChange,
    errors,
    touched,
    handleBlur,
    values,
    } = formik;

  return (
    <div className="auth">
      <form onSubmit={handleSubmit}>
        <h1>Registro</h1>
        <div>
          <label>Nombre de usuario</label>
          <input
            type="text"
            name="userName"
            onChange={handleChange}
            value={values.userName}
            className={errors.userName && touched.userName ? "error" : ""}
            onBlur={handleBlur}
          />
          {errors.userName && touched.userName && (
            <span className="error-message">{errors.userName}</span>
          )}
        </div>
        <div>
          <label>Contraseña</label>
          <input
            type="password"
            name="password"
            onChange={handleChange}
            value={values.password}
            className={errors.password && touched.password ? "error" : ""}
            onBlur={handleBlur}
          />
          {errors.password && touched.password && (
            <span className="error-message">{errors.password}</span>
          )}
        </div>
        <div>
          <label>Email</label>
          <input
            type="email"
            name="email"
            onChange={handleChange}
            value={values.email}
            className={errors.email && touched.email ? "error" : ""}
            onBlur={handleBlur}
          />
          {errors.email && touched.email && (
            <span className="error-message">{errors.email}</span>
          )}
        </div>
        <FormControlLabel
          control={
            <Switch
              value={values.switch}
              onChange={() =>
                formik.setFieldValue("switch", !formik.values.switch)
              }
              name="switch"
              color="secondary"
            />
          }
          label="Pertenecés a un equipo ya creado"
        />
        {values.switch && (
          <div>
            <label>Por favor, introduce el identificador de equipo</label>
            <input
              type="text"
              name="teamID"
              value={values.teamID}
              onChange={handleChange}
            />
          </div>
        )}
        <div>
          <label>Rol</label>
          <select
            name="role"
            onChange={handleChange}
            value={values.role}
            className={errors.role && touched.role ? "error" : ""}
            onBlur={handleBlur}
          >
            <option value="">Seleccionar rol...</option>
            {data?.Rol?.map((option) => (
              <option value={option} key={option}>
                {option}
              </option>
            ))}
          </select>
          {errors.role && touched.role && (
            <span className="error-message">{errors.role}</span>
          )}
        </div>
        
        <div>
          <button type="submit">Enviar</button>
        </div>
        <div>
          <Link to="/login">Ir a Iniciar sesión</Link>
        </div>
      </form>
    </div>
  );
};
