import avengers from '../../../../img/avengers.png';
import spiderman from '../../../../img/spiderman.png';
import blackwidow from '../../../../img/blackWidow.png';
import captainAmerica from '../../../../img/captainAmerica.png';
import hulk from '../../../../img/hulk.png';
import ironman from '../../../../img/ironman.png';
import thor from '../../../../img/thor.png';
import hawkeye from '../../../../img/hawkeye.png';
import Carousel from 'react-bootstrap/Carousel';
import {useState, useEffect} from 'react'
import CarouselItem from 'react-bootstrap/esm/CarouselItem';

function CarouselFade() {

    const [slide, setSlide]= useState('avengers')

    function handleSelect(){
        const img= document.getElementsByTagName('img'); 
        const id= img.src
        console.log(id)
        return id

    }

    useEffect(()=>{
        handleSelect()
    })

  return (
    <Carousel fade inteval={1000} indicators={false} controls={false} onSelect={handleSelect}>
      <Carousel.Item>
        <img
           id='Black Widow'
          className="d-block w-100"
          src={blackwidow}
          alt="First slide"
        />
       
      </Carousel.Item>
      <Carousel.Item>
        <img
        id='Spider Man'
          className="d-block w-100"
          src={spiderman}
          alt="Second slide"
        />

       
      </Carousel.Item>
      <Carousel.Item>
        <img
        id='Hulk'
          className="d-block w-100"
          src={hulk}
          alt="Third slide"
        />

        
      </Carousel.Item>
    </Carousel>
  );
}

export default CarouselFade;
