import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import {axios} from "axios"
import {useState, useEffect} from "react"

import "./Header.styles.css";
import logo from '../../img/Logo.jpg'
import Swal from 'sweetalert2'

const { REACT_APP_API_ENDPOINT: API_ENDPOINT } = process.env;

export const Header = () => {
  const navigate = useNavigate();

  const userName = localStorage.getItem('userName');


  const { tasks } = useSelector((state) => {
    return state.tasksReducer;
  });


  const handleLogout = () => {
    Swal.fire({
      title: '¿Ya está todo planificado?',
      showDenyButton: true,
      showCancelButton: true,
      confirmButtonText: 'Listos!',
      denyButtonText: `Aún me falta...`,
    }).then((result) => {
      
      if (result.isConfirmed) {
        Swal.fire('Perfecto!', 'A salvar el mundo', 'success')
        localStorage.removeItem("token");
       localStorage.removeItem("userName");
       navigate("/login", { replace: true });
      } else if (result.isDenied) {
        Swal.fire('Ok... a seguir planificando!', '', 'info')
      }
    })
  
  };

  const LoadImg=(userName)=>{

    const [data, setData] = useState([])

    axios.get(`${API_ENDPOINT}api/result`)
    
         .then((result) => {
           console.log(result)
           setData(result.data)}
         );

    data.filter((el, index)=>el[index].userName===userName).map((el, index)=>{
      
      return <img src={el[index].url} alt={el[index].userName}/>
    })


   
  }

  return (
    <header>
      <img className='logo' src={logo} alt="Logo" />
       
      <div className="wrapper_right_header">
        
        <div className="textdata">Estrategias creadas: {!tasks ? 0 : tasks.length}</div>
        <div className="textdata">Avenger: {userName}</div>
        <div>{LoadImg(userName)}</div>
        <button className='buttonHeader' onClick={handleLogout}>Cerrar Sesión</button>
      </div>
       
    
    </header>
  );
};
